#[allow(dead_code)]
fn find_floor(directions: &str) -> i32 {
    let mut floor = 0;
    for c in directions.bytes() {
        match c {
            b'(' => floor += 1,
            b')' => floor -= 1,
            _ => (),
        }
    }
    floor
}

#[allow(dead_code)]
fn find_basement_pos(directions: &str) -> usize {
    let mut floor = 0;
    for (i, c) in directions.bytes().enumerate() {
        match c {
            b'(' => floor += 1,
            b')' => floor -= 1,
            _ => (),
        }
        if floor == -1 {
            return i + 1;
        }
    }
    directions.len() + 1
}

#[cfg(test)]
mod solution {
    use super::*;

    #[test]
    fn find_floor_and_basement_pos() {
        let floor = find_floor(DIRECTIONS);
        assert_eq!(floor, 232);

        let basement_pos = find_basement_pos(DIRECTIONS);
        assert_eq!(basement_pos, 1783);
    }
}
