#[allow(dead_code)]
fn find_floor(directions: &str) -> i32{
    let mut floor = 0;
    for c in directions.bytes() {
        match c {
            b'(' => floor = floor + 1,
            b')' => floor = floor - 1,
            _ => (),
        }
    }
    return floor;
}


